CREATE OR REPLACE FUNCTION obter_proc_faturamento2 (nr_seq_proc_interno_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE



cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;


BEGIN

SELECT 	coalesce(MAX(cd_procedimento),0),
	coalesce(MAX(ie_origem_proced),0)
INTO STRICT	cd_procedimento_w,
	ie_origem_proced_w
FROM	proc_interno
