SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_proc_gestao (cd_setor_atendimento_p bigint, nr_atendimento_p bigint, ie_somente_exec_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

				 
ie_existe_proc_w	varchar(1);
				

BEGIN 
ie_existe_proc_w	:= 'N';
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then	 
	 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_existe_proc_w 
		from	gestao_servicos_v 
		where	dt_validade_prescr	> clock_timestamp() 
		and	nr_atendimento		= nr_atendimento_p 
		and	ie_tipo_item		= 'P' 
		and	((ie_somente_exec_p	= 'S' AND dt_fim_horario IS NOT NULL AND dt_fim_horario::text <> '') or 
			((ie_somente_exec_p	= 'N') and (coalesce(dt_fim_horario::text, '') = '')) or (ie_somente_exec_p	= 'A')) 
		and	obter_se_proc_classe(nr_seq_proc_interno, nm_usuario_p) = 'S';
		 
		if (ie_existe_proc_w = 'N') then 
			select	coalesce(max('S'),'N') 
			into STRICT	ie_existe_proc_w 
			from	gestao_servicos_v 
			where	dt_validade_prescr	> clock_timestamp() 
			and	nr_atendimento		= nr_atendimento_p 
			and	ie_tipo_item		= 'E' 
			and	((ie_somente_exec_p	= 'S' AND dt_fim_horario IS NOT NULL AND dt_fim_horario::text <> '') or 
				((ie_somente_exec_p	= 'N') and (coalesce(dt_fim_horario::text, '') = '')) or (ie_somente_exec_p	= 'A'));
		--	and	obter_se_proc_classe(nr_seq_proc_interno, nm_usuario_p) = 'S'; 
		end if;
		 
end if;
 
return	ie_existe_proc_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
