CREATE OR REPLACE FUNCTION obter_proc_interno ( nr_seq_proc_interno_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_resultado_w	varchar(20);

/* Valores possíveis para o parâmetro IE_OPCAO_P
	- 'CI' - Código de integraçao (CD_INTEGRACAO)
*/
BEGIN

if (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') then
	if (ie_opcao_p = 'CI') then
		select	cd_integracao
		into STRICT	ds_resultado_w
		from	proc_interno
		where	nr_sequencia	= nr_seq_proc_interno_p;
	end if;
end if;

return	ds_resultado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
