CREATE OR REPLACE FUNCTION obter_proc_paciente_valor (nr_seq_procedimento_p bigint, ie_tipo_valor_p bigint, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao_p :
1 - VL_PRCOCEDIMENTO
*/
vl_procedimento_w	double precision;
vl_retorno_w		double precision;


BEGIN

select	coalesce(sum(vl_procedimento),0)
into STRICT	vl_procedimento_w
from	proc_paciente_valor
where	nr_seq_procedimento	= nr_seq_procedimento_p
and	ie_tipo_valor		= ie_tipo_valor_p;

vl_retorno_w		:= 0;
if (ie_opcao_p = 1) then
	vl_retorno_w	:= vl_procedimento_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
