CREATE OR REPLACE FUNCTION obter_proc_principal_orcamento ( nr_sequencia_orc_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* IE_OPCAO_P
	C - Código do proc principal
	D - Descrição do proc principal
*/
cd_procedimento_w	bigint;
vl_procedimento_W	double precision;
ds_retorno_w		varchar(255);
ds_procedimento_w	PROCEDIMENTO.DS_PROCEDIMENTO%type;

C01 CURSOR FOR
	SELECT	cd_procedimento,
		obter_descricao_procedimento(cd_procedimento, ie_origem_proced)
	from	orcamento_paciente_proc
	where	nr_sequencia_orcamento = nr_Sequencia_orc_p
	and 	coalesce(ie_procedimento_principal,'N') = 'S'
	order by vl_procedimento asc;


BEGIN

cd_procedimento_w:= '';
ds_procedimento_w:= '';

open C01;
loop fetch C01 into
	cd_procedimento_w,
	ds_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	cd_procedimento_w:= cd_procedimento_w;
	ds_procedimento_w:= ds_procedimento_w;
	end;
end loop;
close c01;

if (ie_opcao_p = 'C') then
	ds_retorno_w:= cd_procedimento_w;
else
	ds_retorno_w:= ds_procedimento_w;
end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
