CREATE OR REPLACE FUNCTION obter_proc_princ_autor (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


cd_proc_principal_w	procedimento.cd_procedimento%type;


BEGIN
select	max(cd_procedimento_principal)
into STRICT	cd_proc_principal_w
from	autorizacao_convenio
where	nr_atendimento		= nr_atendimento_p
and	nr_sequencia		=	(SELECT min(b.nr_sequencia)
					from  	autorizacao_convenio b
					where	b.nr_atendimento	= nr_atendimento_p
					and	b.ie_tipo_autorizacao	= '1'
					and	(b.cd_procedimento_principal IS NOT NULL AND b.cd_procedimento_principal::text <> ''));

return	cd_proc_principal_w;

end;
