CREATE OR REPLACE FUNCTION obter_proc_princ_cirurgia (nr_cirurgia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255):= '';
cd_proc_princ_cirurgia_w	bigint;
ds_proc_princ_cirurgia_w	varchar(60);


BEGIN

if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then

	select 	CD_PROCEDIMENTO_PRINC,
			substr(obter_exame_agenda(cd_procedimento_princ, ie_origem_proced, nr_seq_proc_interno),1,60)
	into STRICT	cd_proc_princ_cirurgia_w,
			ds_proc_princ_cirurgia_w
	from 	cirurgia
	where 	nr_cirurgia = nr_cirurgia_p;

	if (ie_opcao_p = 'C') then
		ds_retorno_w:= cd_proc_princ_cirurgia_w;
	elsif (ie_opcao_p = 'D') then
		ds_retorno_w:= ds_proc_princ_cirurgia_w;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
