CREATE OR REPLACE FUNCTION obter_proc_princ_vinculado ( nr_sequencia_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'S';
qt_existe_w		bigint := 0;
cd_grupo_proc_w		bigint;
cd_especialidade_w	integer;
cd_area_procedimento_w	integer;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	regra_ajuste_mat_proc_vinc a,
	regra_ajuste_material b
where	a.nr_seq_regra_ajuste = b.nr_sequencia
and	b.nr_sequencia = nr_sequencia_p;

if (qt_existe_w > 0) then

	select	coalesce(max(cd_area_procedimento),0),
		coalesce(max(cd_especialidade),0),
		coalesce(max(cd_grupo_proc),0)
	into STRICT	cd_area_procedimento_w,
		cd_especialidade_w,
		cd_grupo_proc_w
	from	estrutura_procedimento_v
	where	cd_procedimento = cd_procedimento_p
	and 	ie_origem_proced = ie_origem_proced_p;

	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	regra_ajuste_mat_proc_vinc a,
		regra_ajuste_material b
	where	a.nr_seq_regra_ajuste = b.nr_sequencia
	and	b.nr_sequencia = nr_sequencia_p
	and	((coalesce(a.cd_procedimento::text, '') = '') or (coalesce(a.cd_procedimento, coalesce(cd_procedimento_p,0)) = coalesce(cd_procedimento_p,0)))
	and	((coalesce(a.ie_origem_proced::text, '') = '') or (coalesce(a.ie_origem_proced, coalesce(ie_origem_proced_p,0)) = coalesce(ie_origem_proced_p,0)))
	and	((coalesce(a.cd_area_procedimento::text, '') = '') or (coalesce(a.cd_area_procedimento, coalesce(cd_area_procedimento_w,0)) = coalesce(cd_area_procedimento_w,0)))
	and	((coalesce(a.cd_especialidade::text, '') = '') or (coalesce(a.cd_especialidade, coalesce(cd_especialidade_w,0)) = coalesce(cd_especialidade_w,0)))
	and	((coalesce(a.cd_grupo_proc::text, '') = '') or (coalesce(a.cd_grupo_proc, coalesce(cd_grupo_proc_w,0)) = coalesce(cd_grupo_proc_w,0)));

end if;

return	ie_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
