SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_produtos_localizacao (nr_seq_localizacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
cd_cnpj_w			varchar(14);


BEGIN

select	max(cd_cnpj)
into STRICT	cd_cnpj_w
from	man_localizacao
where	nr_sequencia = nr_seq_localizacao_p;

select 	rtrim(xmlagg(XMLELEMENT(name e, substr(obter_valor_dominio(2668, ie_produto),1,255) || '/')
  order by substr(obter_valor_dominio(2668, ie_produto),1,255) desc).extract('//text()'), '/')
	into STRICT 	ds_retorno_w
	from	com_cliente
	where cd_cnpj = cd_cnpj_w
	and		ie_produto in ('P','O','M');

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
