SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_prod_financ (nr_documento_p bigint, ie_tipo_documento_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_tipo_documento_p
TR	titulo_receber
TP	titulo_pagar
CH	cheque_cr
AC	movto_cartao_cr
AD	adiantamento
CR	cobranca
NF	Nota fiscal
MV	movto_trans_financ
PR	Perda do contas a receber

ie_opcao_p
P	Código do produto financeiro
S	Código do subgrupo do produto financeiro
G	Código do grupo do produto financeiro
*/
nr_seq_produto_w		bigint;
ds_retorno_w		varchar(255);
nr_titulo_w		bigint;
nr_seq_cheque_w		bigint;
nr_adiantamento_w		bigint;
nr_seq_movto_cartao_w	bigint;
nr_seq_titulo_receber_w	bigint;
nr_seq_titulo_pagar_w	bigint;

ie_produto_financ_w	varchar(1);


BEGIN

begin
select	'S'
into STRICT	ie_produto_financ_w
from	produto_financeiro LIMIT 1;
exception
when no_data_found then
	ie_produto_financ_w := 'N';
end;

if (ie_produto_financ_w = 'S') then
	begin
	if (ie_tipo_documento_p = 'TR') then

		select	max(nr_seq_produto)
		into STRICT	nr_seq_produto_w
		from	titulo_receber_classif
		where	nr_titulo	= nr_documento_p;

	elsif (ie_tipo_documento_p = 'TP') then

		select	max(nr_seq_produto)
		into STRICT	nr_seq_produto_w
		from	titulo_pagar_classif
		where	nr_titulo	= nr_documento_p;

	elsif (ie_tipo_documento_p = 'CH') then

		select	max(nr_seq_produto)
		into STRICT	nr_seq_produto_w
		from	cheque_cr
		where	nr_seq_cheque	= nr_documento_p;

	elsif (ie_tipo_documento_p = 'AC') then

		select	max(nr_seq_produto)
		into STRICT	nr_seq_produto_w
		from	movto_cartao_cr_classif
		where	nr_seq_movto   	= nr_documento_p;

	elsif (ie_tipo_documento_p = 'AD') then

		select	max(nr_seq_produto)
		into STRICT	nr_seq_produto_w
		from	adiantamento_classif
		where	nr_adiantamento   = nr_documento_p;

	elsif (ie_tipo_documento_p = 'CR') then

		select	max(nr_titulo),
			max(nr_seq_cheque)
		into STRICT	nr_titulo_w,
			nr_seq_cheque_w
		from	cobranca
		where	nr_sequencia	= nr_documento_p;

		if (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then
			select	max(nr_seq_produto)
			into STRICT	nr_seq_produto_w
			from	titulo_receber_classif
			where	nr_titulo	= nr_titulo_w;
		elsif (nr_seq_cheque_w IS NOT NULL AND nr_seq_cheque_w::text <> '') then
			select	max(nr_seq_produto)
			into STRICT	nr_seq_produto_w
			from	cheque_cr
			where	nr_seq_cheque	= nr_seq_cheque_w;
		end if;
	elsif (ie_tipo_documento_p = 'NF') then

		select	max(nr_seq_produto)
		into STRICT	nr_seq_produto_w
		from	nota_fiscal_item
		where	nr_sequencia   = nr_documento_p;
	elsif (ie_tipo_documento_p = 'MV') then

		select	max(nr_adiantamento),
			max(nr_seq_cheque),
			max(nr_seq_movto_cartao),
			max(nr_seq_titulo_receber),
			max(nr_seq_titulo_pagar)
		into STRICT	nr_adiantamento_w,
			nr_seq_cheque_w,
			nr_seq_movto_cartao_w,
			nr_seq_titulo_receber_w,
			nr_seq_titulo_pagar_w
		from	movto_trans_financ
		where	nr_sequencia	= nr_documento_p;

		if (nr_adiantamento_w IS NOT NULL AND nr_adiantamento_w::text <> '') then
			nr_seq_produto_w	:= OBTER_PROD_FINANC(nr_adiantamento_w,'AD',ie_opcao_p);
		elsif (nr_seq_cheque_w IS NOT NULL AND nr_seq_cheque_w::text <> '') then
			nr_seq_produto_w	:= OBTER_PROD_FINANC(nr_seq_cheque_w,'CH',ie_opcao_p);
		elsif (nr_seq_movto_cartao_w IS NOT NULL AND nr_seq_movto_cartao_w::text <> '') then
			nr_seq_produto_w	:= OBTER_PROD_FINANC(nr_seq_movto_cartao_w,'AC',ie_opcao_p);
		elsif (nr_seq_titulo_receber_w IS NOT NULL AND nr_seq_titulo_receber_w::text <> '') then
			nr_seq_produto_w	:= OBTER_PROD_FINANC(nr_seq_titulo_receber_w,'TR',ie_opcao_p);
		elsif (nr_seq_titulo_pagar_w IS NOT NULL AND nr_seq_titulo_pagar_w::text <> '') then
			nr_seq_produto_w	:= OBTER_PROD_FINANC(nr_seq_titulo_pagar_w,'TP',ie_opcao_p);
		end if;
	elsif (ie_tipo_documento_p = 'PR') then

		select	max(nr_seq_cheque),
			max(nr_titulo)
		into STRICT	nr_seq_cheque_w,
			nr_seq_titulo_receber_w
		from	perda_contas_receber
		where	nr_sequencia	= nr_documento_p;

		if (nr_seq_cheque_w IS NOT NULL AND nr_seq_cheque_w::text <> '') then
			nr_seq_produto_w	:= OBTER_PROD_FINANC(nr_seq_cheque_w,'CH',ie_opcao_p);
		elsif (nr_seq_titulo_receber_w IS NOT NULL AND nr_seq_titulo_receber_w::text <> '') then
			nr_seq_produto_w	:= OBTER_PROD_FINANC(nr_seq_titulo_receber_w,'TR',ie_opcao_p);
		end if;

	end if;


	if (ie_opcao_p = 'P') then
		ds_retorno_w	:= to_char(nr_seq_produto_w);
	elsif (ie_opcao_p = 'S') then
		select	to_char(max(nr_seq_grupo))
		into STRICT	ds_retorno_w
		from	produto_financeiro
		where	nr_sequencia		= nr_seq_produto_w;
	elsif (ie_opcao_p = 'G') then
		select	to_char(max(b.nr_seq_grupo_sup))
		into STRICT	ds_retorno_w
		from	grupo_prod_financ b,
			produto_financeiro a
		where	a.nr_seq_grupo		= b.nr_sequencia
		and	a.nr_sequencia		= nr_seq_produto_w;
	end if;
	end;
end if;

if (coalesce(nr_seq_produto_w::text, '') = '') then
	ds_retorno_w	:= 99999; --Não informado (Utilizado para os relatórios do HSL).
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_prod_financ (nr_documento_p bigint, ie_tipo_documento_p text, ie_opcao_p text) FROM PUBLIC;

