SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_prod_prescr_leite ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(2000);
ds_material_w	varchar(255);
ds_nr_sequencia_w bigint;

c01 CURSOR FOR
	SELECT	distinct substr(obter_desc_material(a.cd_material),1,255),
			a.nr_sequencia
	from	prescr_material a,
		prescr_leite_deriv b
	where	a.nr_seq_leite_deriv 	= nr_sequencia_p
	and	a.nr_prescricao		= nr_prescricao_p
	and	b.nr_sequencia 		= a.nr_seq_leite_deriv
	
union

	SELECT	distinct substr(obter_desc_material(a.cd_material),1,255),
			a.nr_sequencia
	from	prescr_material a,
		prescr_leite_deriv b
	where	a.nr_prescricao	= nr_prescricao_p
	and		a.nr_prescricao = b.nr_prescricao
	and		ie_agrupador = 17
	and	    (a.nr_sequencia_diluicao IS NOT NULL AND a.nr_sequencia_diluicao::text <> '')
	and		exists (select 1 from prescr_material c where a.nr_sequencia_diluicao  =  c.nr_sequencia and c.nr_seq_leite_deriv = nr_sequencia_p)
    order by 2;

BEGIN

open C01;
loop
fetch C01 into
	ds_material_w,
	ds_nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := ds_material_w;
	else
		ds_retorno_w := ds_retorno_w ||chr(13) ||chr(10) || ds_material_w;
	end if;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
