CREATE OR REPLACE FUNCTION obter_profissional_atend_aux (nr_atendimento_p bigint, ie_profissional_p text, ie_opcao_p text, dt_inicio_p timestamp, nr_seq_turno_p nurse_shift_user_daily.nr_seq_master_shift%type) RETURNS varchar AS $body$
DECLARE


/*
C	- Codigo
N	- Nome
P   - PHS
*/
ds_retorno_w		varchar(60);
cd_profissional_w	varchar(10);
nr_phs_w            phs_phone_number.nr_phs%type;

c01 CURSOR FOR
SELECT b.cd_pessoa_fisica, c.nr_phs
FROM atendimento_paciente a, atend_profissional b
LEFT OUTER JOIN phs_phone_number c ON (b.nr_seq_phs = c.nr_sequencia)
WHERE a.nr_atendimento    = nr_atendimento_p and a.nr_atendimento    = b.nr_atendimento and b.ie_profissional   = coalesce(ie_profissional_p,'P')  and trunc(b.dt_inicio_vigencia) between trunc(a.dt_entrada) and trunc(dt_inicio_p) and ((coalesce(NR_SEQ_TURNO_P::text, '') = '' and 1 = 1)
or ((NR_SEQ_TURNO_P IS NOT NULL AND NR_SEQ_TURNO_P::text <> '')
         and exists (select 1
                     from nurse_shift_user x, nurse_shift_user_daily y
                     where y.nr_seq_nurse_shift = x.nr_sequencia
                     and x.cd_pessoa_fisica = b.cd_pessoa_fisica
                     and y.nr_seq_master_shift = NR_SEQ_TURNO_P))) order by
    b.dt_inicio_vigencia;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	open c01;
	loop
	fetch c01 into	cd_profissional_w, nr_phs_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		cd_profissional_w	:= cd_profissional_w;
		end;
	end loop;
	close c01;

	if (cd_profissional_w IS NOT NULL AND cd_profissional_w::text <> '') then
		if (coalesce(ie_opcao_p,'N') = 'N') then
			ds_retorno_w	:= substr(obter_nome_pf(cd_profissional_w),1,60);
        elsif (coalesce(ie_opcao_p,'N')  = 'P') then
            ds_retorno_w := nr_phs_w;
		else
			ds_retorno_w	:= cd_profissional_w;
		end if;
	end if;
end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
