CREATE OR REPLACE FUNCTION obter_profissional_atend (nr_atendimento_p bigint, ie_profissional_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
C	- CÃ³digo
N	- Nome
*/
ds_retorno_w		varchar(60);
cd_profissional_w	varchar(10);

c01 CURSOR FOR
SELECT	b.cd_pessoa_fisica
from	atendimento_paciente a,
	atend_profissional b
where	a.nr_atendimento	= nr_atendimento_p
and	a.nr_atendimento	= b.nr_atendimento
and	b.ie_profissional	= coalesce(ie_profissional_p,'P')
and	b.dt_inicio_vigencia	between a.dt_entrada and clock_timestamp()
order by
	b.dt_inicio_vigencia;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	open c01;
	loop
	fetch c01 into	cd_profissional_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		cd_profissional_w	:= cd_profissional_w;
		end;
	end loop;
	close c01;

	if (cd_profissional_w IS NOT NULL AND cd_profissional_w::text <> '') then
		if (coalesce(ie_opcao_p,'N') = 'N') then
			ds_retorno_w	:= substr(obter_nome_pf(cd_profissional_w),1,60);
		else
			ds_retorno_w	:= cd_profissional_w;
		end if;
	end if;
end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
