CREATE OR REPLACE FUNCTION obter_profissional_resp ( nr_atendimento_p bigint, ie_profissional_p text ) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);


BEGIN

/*
E - Enfermeiro
F - Farmacêutico
M - Médico
N - Nutricionista
P - Psicólogo
*/
select	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	atend_profissional
where	nr_atendimento		=	nr_atendimento_p
and	ie_profissional		=	ie_profissional_p
and	dt_inicio_vigencia	=	(SELECT	max(dt_inicio_vigencia)
					from	atend_profissional
					where	nr_atendimento		=	nr_atendimento_p
					and	ie_profissional		=	ie_profissional_p);

return	cd_pessoa_fisica_w;

end;
