CREATE OR REPLACE FUNCTION obter_profissional_usuario (nm_usuario_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_profissional_w  varchar(15);
ds_profissional_w  varchar(200);


BEGIN

select max(ie_profissional)
into STRICT ie_profissional_w
from usuario
where nm_usuario  = nm_usuario_p;

if (ie_opcao_p  = 'C') then
  return ie_profissional_w;
else
  select substr(obter_valor_dominio(1631, ie_profissional_w), 1, 200)
  into STRICT ds_profissional_w
;

  return ds_profissional_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
