CREATE OR REPLACE FUNCTION obter_prof_agenda_atend ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(200);	
cd_pessoa_fisica_w		varchar(10);		
			 

BEGIN 
 
begin 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
 
	select	max(b.cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from	agenda_consulta a, 
			agenda b 
	where	a.cd_agenda = b.cd_agenda 
	and		a.nr_atendimento	= nr_atendimento_p;
	 
	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
		 
		ds_retorno_w := substr(obter_nome_medico(cd_pessoa_fisica_w,'N'),1,200);
		 
	end if;
end if;
 
 
exception 
when others then 
		null;
end;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
