CREATE OR REPLACE FUNCTION obter_prof_agenda_cir ( nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_profissional_w	varchar(4000):= '';
nm_profissional_w	varchar(60):='';

c01 CURSOR FOR 
	SELECT	obter_initcap(substr(obter_nome_pf(cd_pessoa_fisica),1,60)) nm_profissional 
	from	pessoa_fisica b, 
		profissional_agenda a 
	where	b.cd_pessoa_fisica = a.cd_profissional 
	and	a.nr_seq_agenda = nr_seq_agenda_p 
	and	ie_indicacao = 'S' 
	order by nm_pessoa_fisica desc;


BEGIN 
 
open c01;
loop 
 
	fetch c01 into	nm_profissional_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
 
	if (nm_profissional_w IS NOT NULL AND nm_profissional_w::text <> '') then 
		if (coalesce(ds_profissional_w::text, '') = '') then 
			ds_profissional_w	:=	nm_profissional_w;
		else 
			ds_profissional_w	:=	nm_profissional_w || ', ' || ds_profissional_w;
		end if;
	end if;
 
end loop;
close c01;
 
return	ds_profissional_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_prof_agenda_cir ( nr_seq_agenda_p bigint) FROM PUBLIC;

