SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_prof_area_parecer ( cd_especialidade_p bigint, nr_seq_area_atuacao_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_escala_w			bigint;
cd_pessoa_fisica_w		varchar(10);
nr_seq_escala_diaria_w	bigint;
cd_estabelecimento_w	smallint;


BEGIN
cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

select	max(a.nr_seq_escala)
into STRICT	nr_seq_escala_w
from	regra_profissional_parecer a,
	escala b,
	escala_diaria c
where	a.nr_seq_escala = b.nr_sequencia
and	b.nr_sequencia = c.nr_seq_escala
and	cd_especialidade = cd_especialidade_p
and	coalesce(nr_seq_area_atuacao,0) = coalesce(nr_seq_area_atuacao_p,0)
and coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
and	clock_timestamp() between coalesce(dt_inicio,clock_timestamp()) and coalesce(dt_fim,clock_timestamp());


if (coalesce(nr_seq_escala_w,0) > 0) then
	select	max(cd_pessoa_fisica),
			max(nr_sequencia)
	into STRICT	cd_pessoa_fisica_w,
			nr_seq_escala_diaria_w
	from	escala_diaria
	where	nr_seq_escala = nr_seq_escala_w
	and		clock_timestamp() between dt_inicio and dt_fim;

	if (coalesce(cd_pessoa_fisica_w::text, '') = '') then
		select	max(cd_pessoa_fisica)
		into STRICT	cd_pessoa_fisica_w
		from	escala_diaria_adic
		where	nr_seq_escala_diaria = nr_seq_escala_diaria_w;
	end if;

end if;

return	cd_pessoa_fisica_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
