CREATE OR REPLACE FUNCTION obter_prof_atend_vig (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_profissional_w	atend_profissional.cd_pessoa_fisica%type;

c01 CURSOR FOR
SELECT	b.cd_pessoa_fisica
from	atendimento_paciente a,
	atend_profissional b
where	a.nr_atendimento	= nr_atendimento_p
and	a.nr_atendimento	= b.nr_atendimento
and	b.dt_inicio_vigencia	between a.dt_entrada and clock_timestamp()
order by
	b.dt_inicio_vigencia desc;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	open c01;
	loop
	fetch c01 into	cd_profissional_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			if (cd_profissional_w IS NOT NULL AND cd_profissional_w::text <> '') then
				return substr(obter_nome_pf(cd_profissional_w),1,254);
			end if;
		end;
	end loop;
	close c01;

end if;

return	cd_profissional_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
