SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_prof_cirurgia_func ( nr_cirurgia_p bigint, ie_funcao_p text) RETURNS varchar AS $body$
DECLARE


ds_profissional_w	varchar(4000):= '';
nm_profissional_w	varchar(60):='';

/* 'C' - Circulante
'A' - Auxiliar
'I - Instrumentador
O - Outros */
c01 CURSOR FOR
	SELECT	obter_initcap(substr(obter_nome_pf(cd_pessoa_fisica),1,60)) nm_profissional
	from	cirurgia_participante a, funcao_medico b
	where	a.nr_cirurgia = nr_cirurgia_p
	and 	a.ie_funcao = to_char(b.cd_funcao)
	and	IE_INSTRUMENTADOR	= 'S'
	and	ie_funcao_p		= 'I'
	
union

	SELECT	obter_initcap(substr(obter_nome_pf(cd_pessoa_fisica),1,60)) nm_profissional
	from	cirurgia_participante a, funcao_medico b
	where	a.nr_cirurgia 	= nr_cirurgia_p
	and 	a.ie_funcao 	= to_char(b.cd_funcao)
	and	IE_INSTRUMENTADOR	= 'N'
	and	ie_medico		= 'N'
	and	IE_ANESTESISTA 		= 'N'
	and	IE_AUXILIAR    		= 'N'
	and	ie_funcao_p		= 'C'
	
union

	select	obter_initcap(substr(obter_nome_pf(cd_pessoa_fisica),1,60)) nm_profissional
	from	cirurgia_participante a, funcao_medico b
	where	a.nr_cirurgia = nr_cirurgia_p
	and 	a.ie_funcao = to_char(b.cd_funcao)
	and	IE_INSTRUMENTADOR	= 'N'
	and (ie_medico		= 'S' or IE_ANESTESISTA 		= 'S' or IE_AUXILIAR    		= 'S')
	and	ie_funcao_p		= 'O'
	order by	1 desc;


BEGIN

if (coalesce(nr_cirurgia_p,0) > 0) then
	open c01;
	loop

		fetch c01 into	nm_profissional_w;
			EXIT WHEN NOT FOUND; /* apply on c01 */

		if (nm_profissional_w IS NOT NULL AND nm_profissional_w::text <> '') then
			if (coalesce(ds_profissional_w::text, '') = '') then
				ds_profissional_w	:=	nm_profissional_w;
			else
				ds_profissional_w	:=	nm_profissional_w || ', ' || ds_profissional_w;
			end if;
		end if;

	end loop;
	close c01;
end if;

return	ds_profissional_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_prof_cirurgia_func ( nr_cirurgia_p bigint, ie_funcao_p text) FROM PUBLIC;

