SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_prof_classe (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

 
nm_usuario_w		varchar(15);
ie_tipo_evolucao_w	varchar(3);
ds_classe_w			varchar(255);
nm_pf_w				varchar(255);


BEGIN 
select	max(nm_usuario_nrec) 
into STRICT	nm_usuario_w 
from	historico_saude_cirurgia 
where	nr_sequencia = nr_sequencia_p;
 
select	max(ie_tipo_evolucao), 
		max(substr(obter_nome_pf(cd_pessoa_fisica),1,255)) 
into STRICT	ie_tipo_evolucao_w, 
		nm_pf_w 
from	usuario 
where	nm_usuario = nm_usuario_w;
 
select	max(substr(obter_valor_dominio(72,ie_tipo_evolucao_w),1,255)) 
into STRICT	ds_classe_w
;
 
return	nm_pf_w || ' - ' || ds_classe_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_prof_classe (nr_sequencia_p bigint) FROM PUBLIC;

