CREATE OR REPLACE FUNCTION obter_prof_conta_checkup (nr_seq_checkup_etapa_p bigint) RETURNS varchar AS $body$
DECLARE

 
nm_medico_executor_w	varchar(255);			

BEGIN 
 
select max(substr(obter_nome_pf(b.cd_pessoa_fisica),1,255)) 
into STRICT  nm_medico_executor_w 
from  procedimento_paciente a, 
    pessoa_fisica b 
where a.nr_seq_checkup_etapa = nr_seq_checkup_etapa_p 
and  a.cd_medico_executor = b.cd_pessoa_fisica;
 
if (coalesce(nm_medico_executor_w::text, '') = '') then 
	select max(substr(obter_nome_pf(b.cd_pessoa_fisica),1,255)) 
	into STRICT  nm_medico_executor_w 
	from  procedimento_paciente a, 
	    pessoa_fisica b 
	where a.nr_seq_checkup_etapa = nr_seq_checkup_etapa_p 
	and  a.CD_PESSOA_FISICA = b.cd_pessoa_fisica;
end if;	
 
 
return	nm_medico_executor_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
