CREATE OR REPLACE FUNCTION obter_prof_enf_contato_alta (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
nm_prof_w	varchar(60);


BEGIN 
 
	select max(substr(obter_nome_pf(pf.cd_pessoa_fisica),1,60)) 
	into STRICT nm_prof_w 
	from orientacao_alta_enf a, 
		 pessoa_fisica pf 
	where a.cd_profissional = pf.cd_pessoa_fisica 
		 and nr_sequencia = 
		 (SELECT max(nr_sequencia) 
		  from orientacao_alta_enf 
		  where nr_atendimento = nr_atendimento_p 
				 and ie_tipo_registro = 'O' 
				 and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
				 and coalesce(dt_inativacao::text, '') = '');
 
 
return	nm_prof_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
