CREATE OR REPLACE FUNCTION obter_prof_hospitalidade ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

nr_sequencia_w		bigint;
cd_pessoa_fisica_w	varchar(10);


BEGIN

begin
select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	atend_profissional
where	nr_atendimento		= nr_atendimento_p
and	((ie_profissional		= 'H') or (IS_COPY_BASE_LOCALE('es_MX') = 'S' and ie_profissional = '12'));
exception
	when others then
		nr_sequencia_w	:= 0;
end;

if (nr_sequencia_w > 0) then
	select	cd_pessoa_fisica
	into STRICT	cd_pessoa_fisica_w
	from	atend_profissional
	where	nr_sequencia		= nr_sequencia_w;
end if;

return	cd_pessoa_fisica_w;

end;
$body$
