SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_project_estagio_status ( ds_estagio_status_p text, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE

/*
S =  Parametro Status para obter o estágio,
E = Parametro Estágio para obter Status.
*/
qtd_registros_w bigint;
nr_retorno_w bigint;
nr_status_proj_w bigint;

BEGIN
if (ie_tipo_p = 'E') then
	select count(*)
	into STRICT qtd_registros_w
	from arm_stage_status_matrix
	where nr_seq_estagio_pipeline = ds_estagio_status_p;

	if (qtd_registros_w = 1) then
		select nr_seq_status_projeto
		into STRICT nr_retorno_w
		from arm_stage_status_matrix
		where nr_seq_estagio_pipeline = ds_estagio_status_p;
	end if;
end if;

if (ie_tipo_p = 'S') then

	select	max(nr_sequencia)
	into STRICT	nr_status_proj_w
	from	classificacao_projeto a
	where	ie_origem_projeto = 'D'
	and	ie_classificacao = ds_estagio_status_p;

	select count(*)
	into STRICT qtd_registros_w
	from arm_stage_status_matrix
	where nr_seq_status_projeto = nr_status_proj_w;

	if (qtd_registros_w = 1) then
		select nr_seq_estagio_pipeline
		into STRICT nr_retorno_w
		from arm_stage_status_matrix
		where nr_seq_status_projeto = nr_status_proj_w;
	end if;
end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
