CREATE OR REPLACE FUNCTION obter_projeto_nota_fiscal ( nr_seq_nota_fiscal_p bigint ) RETURNS bigint AS $body$
DECLARE

    nr_seq_proj_rec_w   ctb_movimento.nr_seq_proj_rec%type := NULL;
    contador            bigint := 0;

BEGIN
    SELECT
        COUNT(DISTINCT(nr_seq_proj_rec))
    INTO STRICT contador
    FROM
        nota_fiscal_item
    WHERE
        nr_sequencia = nr_seq_nota_fiscal_p
        AND (nr_seq_proj_rec IS NOT NULL AND nr_seq_proj_rec::text <> '');

    IF contador = 1 THEN
        SELECT DISTINCT( nr_seq_proj_rec )
        INTO STRICT nr_seq_proj_rec_w
        FROM
            nota_fiscal_item
        WHERE
            nr_sequencia = nr_seq_nota_fiscal_p
            AND (nr_seq_proj_rec IS NOT NULL AND nr_seq_proj_rec::text <> '');
    END IF;

    RETURN nr_seq_proj_rec_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
