CREATE OR REPLACE FUNCTION obter_projeto_pend_ass_rep ( nr_sequencia_p bigint, nr_prescricao_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_projeto_w		bigint;
nr_seq_dieta_w 			bigint;
nr_seq_proced_w			bigint;
nr_seq_material_w		bigint;
nr_prescricao_w			bigint;
ie_tipo_agrupador_w		varchar(5);
ie_hemocomponente_w		varchar(1);
nr_seq_recomendacao_w	bigint;
nr_seq_solucao_w		bigint;
nr_seq_nut_pac_w		bigint;
nr_seq_nut_prot_w		bigint;
nr_seq_gasoterapia_w	bigint;
ie_npt_adulta_w			varchar(1);

BEGIN

select	max(nr_prescricao),
		coalesce(max(nr_seq_dieta_rep),0),
		coalesce(max(nr_seq_proced_rep),0),
		coalesce(max(nr_seq_material_rep),0),
		coalesce(max(nr_seq_recomendacao),0),
		coalesce(max(nr_seq_solucao),0),
		coalesce(max(nr_seq_nut_pac),0),
		coalesce(max(nr_seq_nut_prot),0),
		coalesce(max(nr_seq_gasoterapia),0)
into STRICT	nr_prescricao_w,
		nr_seq_dieta_w,
		nr_seq_proced_w,
		nr_seq_material_w,
		nr_seq_recomendacao_w,
		nr_seq_solucao_w,
		nr_seq_nut_pac_w,
		nr_seq_nut_prot_w,
		nr_seq_gasoterapia_w
from	pep_item_pendente
where	nr_sequencia = nr_sequencia_p;
--- Prescrição
if (coalesce(nr_prescricao_w,0) > 0) and (nr_seq_dieta_w = 0) and (nr_seq_proced_w = 0) and (nr_seq_recomendacao_w = 0) and (nr_seq_solucao_w = 0) and (nr_seq_nut_pac_w = 0) and (nr_seq_nut_prot_w = 0) and (nr_seq_gasoterapia_w = 0) and (nr_seq_material_w = 0) then
	nr_seq_projeto_w := 1;
--- Tabela PRESCR_MATERIAL
elsif (nr_seq_material_w > 0) and (nr_seq_dieta_w	= 0) and (nr_seq_recomendacao_w = 0) and (nr_seq_solucao_w = 0) and (nr_seq_nut_pac_w = 0) and (nr_seq_nut_prot_w = 0) and (nr_seq_gasoterapia_w = 0) and (nr_seq_proced_w = 0) then

	select	obter_tipo_agrupador(max(ie_agrupador))
	into STRICT	ie_tipo_agrupador_w
	from	prescr_material
	where	nr_sequencia = nr_seq_material_w
	and		nr_prescricao = coalesce(nr_prescricao_w,nr_prescricao_p);

	if (ie_tipo_agrupador_w = 'M') then
		nr_seq_projeto_w := 25406;
	elsif (ie_tipo_agrupador_w = 'SOL') then
		nr_seq_projeto_w := 25405;
	elsif (ie_tipo_agrupador_w = 'MAT') then
		nr_seq_projeto_w := 25407;
	elsif (ie_tipo_agrupador_w = 'S') then
		nr_seq_projeto_w := 25398;
	elsif (ie_tipo_agrupador_w = 'SNE') then
		nr_seq_projeto_w := 25399;
	elsif (ie_tipo_agrupador_w = 'DI') then
		nr_seq_projeto_w := 25414;
	end if;
--- Tabela PRESCR_DIETA
elsif (nr_seq_dieta_w > 0) and (nr_seq_material_w = 0) and (nr_seq_recomendacao_w = 0) and (nr_seq_solucao_w = 0) and (nr_seq_nut_pac_w = 0) and (nr_seq_nut_prot_w = 0) and (nr_seq_gasoterapia_w = 0) and (nr_seq_proced_w = 0) then
	nr_seq_projeto_w := 25396;
--- Tabela PRESCR_PROCEDIMENTO
elsif (nr_seq_proced_w > 0) and (nr_seq_material_w = 0) and (nr_seq_recomendacao_w = 0) and (nr_seq_solucao_w = 0) and (nr_seq_nut_pac_w = 0) and (nr_seq_nut_prot_w = 0) and (nr_seq_gasoterapia_w = 0) and (nr_seq_dieta_w = 0) then

	select	coalesce(max('S'),'N')
	into STRICT	ie_hemocomponente_w
	from	prescr_procedimento
	where	nr_prescricao = coalesce(nr_prescricao_w,nr_prescricao_p)
	and		nr_sequencia = nr_seq_proced_w
	and		(nr_seq_derivado IS NOT NULL AND nr_seq_derivado::text <> '');

	if (ie_hemocomponente_w = 'S') then
		nr_seq_projeto_w := 51568;
	else
		nr_seq_projeto_w := 25408;
	end if;
--- Tabela PRESCR_RECOMENDACAO
elsif (nr_seq_recomendacao_w > 0) and (nr_seq_material_w = 0) and (nr_seq_proced_w = 0) and (nr_seq_solucao_w = 0) and (nr_seq_nut_pac_w = 0) and (nr_seq_nut_prot_w = 0) and (nr_seq_gasoterapia_w = 0) and (nr_seq_dieta_w = 0) then
	nr_seq_projeto_w := 25413;
--- Tabela PRESCR_SOLUCAO
elsif (nr_seq_solucao_w > 0) and (nr_seq_material_w = 0) and (nr_seq_proced_w = 0) and (nr_seq_recomendacao_w = 0) and (nr_seq_nut_pac_w = 0) and (nr_seq_nut_prot_w = 0) and (nr_seq_gasoterapia_w = 0) and (nr_seq_dieta_w = 0) then
	nr_seq_projeto_w := 25405;
--- Tabela PRESCR_GASOTERAPIA
elsif (nr_seq_gasoterapia_w > 0) and (nr_seq_material_w = 0) and (nr_seq_proced_w = 0) and (nr_seq_recomendacao_w = 0) and (nr_seq_nut_pac_w = 0) and (nr_seq_nut_prot_w = 0) and (nr_seq_solucao_w = 0) and (nr_seq_dieta_w = 0) then
	nr_seq_projeto_w := 25411;
--- Tabela NUT_PACIENTE
elsif (nr_seq_nut_pac_w > 0) and (nr_seq_material_w = 0) and (nr_seq_proced_w = 0) and (nr_seq_recomendacao_w = 0) and (nr_seq_gasoterapia_w = 0) and (nr_seq_nut_prot_w = 0) and (nr_seq_solucao_w = 0) and (nr_seq_dieta_w = 0) then
	nr_seq_projeto_w := 25401;
--- Tabela NUT_PAC
elsif (nr_seq_nut_prot_w > 0) and (nr_seq_material_w = 0) and (nr_seq_proced_w = 0) and (nr_seq_recomendacao_w = 0) and (nr_seq_gasoterapia_w = 0) and (nr_seq_nut_pac_w = 0) and (nr_seq_solucao_w = 0) and (nr_seq_dieta_w = 0) then

	select	max(ie_npt_adulta)
	into STRICT	ie_npt_adulta_w
	from	nut_pac
	where	nr_sequencia = nr_seq_nut_prot_w
	and		nr_prescricao = coalesce(nr_prescricao_w,nr_prescricao_p);

	if (ie_npt_adulta_w = 'S') then
		nr_seq_projeto_w := 51529;
	elsif (ie_npt_adulta_w = 'N') then
		nr_seq_projeto_w := 25403;
	elsif (ie_npt_adulta_w = 'P') then
		nr_seq_projeto_w := 51669;
	end if;
end if;

return nr_seq_projeto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
