CREATE OR REPLACE FUNCTION obter_projeto_principal (nr_seq_projeto_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_projeto_w		bigint;


BEGIN

select	min(a.nr_seq_proj)
into STRICT	nr_seq_projeto_w
from	proj_cronograma a,
	proj_cron_etapa b,
	proj_projeto c
where	b.nr_seq_cronograma	= a.nr_sequencia
and	c.nr_sequencia		= a.nr_seq_proj
and	c.nr_seq_estagio	<> 44
and	b.nr_seq_sub_proj	= nr_seq_projeto_p;

return	nr_seq_projeto_w;

end;
