CREATE OR REPLACE FUNCTION obter_proj_rec_item_ordem ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
0 = Codigo projeto
1 = Descricao projeto*/
nr_seq_proj_rec_w				bigint;
ds_projeto_w				varchar(100);


BEGIN

select	nr_seq_proj_rec,
	substr(obter_desc_proj_recurso(nr_seq_proj_rec),1,100) ds_projeto
into STRICT	nr_seq_proj_rec_w,
	ds_projeto_w
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p
and	nr_item_oci = nr_item_oci_p;

if (ie_opcao_p = 0) then
	return nr_seq_proj_rec_w;
elsif (ie_opcao_p = 1) then
	return ds_projeto_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
