SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_pronome_pf (cd_pessoa_fisica_p text, ie_artigo_p text) RETURNS varchar AS $body$
DECLARE



ds_pronome_w		varchar(10);
qt_idade_w			integer;
ie_sexo_w			varchar(01);
ie_estado_civil_w		varchar(02);
ie_artigo_w			varchar(50);


BEGIN

ds_pronome_w		:= '';

select	obter_idade(dt_nascimento, clock_timestamp(), 'A'),
	ie_sexo,
	ie_estado_civil
into STRICT	qt_idade_w,
	ie_sexo_w,
	ie_estado_civil_w
from	pessoa_fisica
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;


if (qt_idade_w > 17) then
	begin
	if (ie_sexo_w	= 'M') then
		begin
		ds_pronome_w	:= obter_desc_expressao(729385);
		ie_artigo_w		:= obter_desc_expressao(729393);
		end;
	elsif (ie_sexo_w	= 'F') then
		begin
		ie_artigo_w			:= obter_desc_expressao(729395);
		if (ie_estado_civil_w	= '1') and (qt_idade_w < 27) then
			ds_pronome_w	:= obter_desc_expressao(729422);
		else
			ds_pronome_w	:= obter_desc_expressao(729424);
		end if;
		end;
	end if;
	end;
end if;


if (ie_artigo_p	= 'S') then
	ds_pronome_w	:= ie_artigo_w || ' ' || ds_pronome_w;
end if;

return ds_pronome_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
