SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_protocolos_adicionais (nr_seq_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_protocolo_w 		varchar(255);
ds_retorno_w		varchar(2000);

c01 CURSOR FOR

SELECT	distinct substr(obter_desc_protocolo(b.cd_protocolo),1,130) || ' / ' ||
		substr(obter_desc_protocolo_medic(nr_seq_medicacao, cd_protocolo),1,125)
from	paciente_protocolo_medic b,
		paciente_atend_medic a
where	a.nr_Seq_int_prot_medic = b.nr_Seq_interno
and		substr(obter_desc_protocolo(b.cd_protocolo),1,130) || ' / ' ||
		substr(obter_desc_protocolo_medic(nr_seq_medicacao, cd_protocolo),1,125) <> ' / '
and		a.nr_seq_atendimento = nr_seq_atendimento_p;


BEGIN
if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then
	open c01;
	loop
	fetch c01 into	ds_protocolo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	ds_retorno_w	:= ds_retorno_w || ds_protocolo_w || ', ';

end loop;
end if;

return	ds_retorno_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_protocolos_adicionais (nr_seq_atendimento_p bigint) FROM PUBLIC;

