CREATE OR REPLACE FUNCTION obter_protocolo_agenda (nr_seq_agenda_quimio_p bigint) RETURNS varchar AS $body$
DECLARE


     
ds_retorno_w   		varchar(500) := ' ';
ds_protocolo_w   		varchar(500);
ds_medic_w   		varchar(500);
cd_protocolo_w 		agenda_quimio.cd_protocolo%type;
nr_seq_medicacao_w 	agenda_quimio.nr_seq_medicacao%type;


BEGIN
	if (nr_seq_agenda_quimio_p IS NOT NULL AND nr_seq_agenda_quimio_p::text <> '') then
	
		select max(cd_protocolo), max(nr_seq_medicacao)
		into STRICT cd_protocolo_w, nr_seq_medicacao_w
		from agenda_quimio where nr_sequencia = nr_seq_agenda_quimio_p;

		if (cd_protocolo_w IS NOT NULL AND cd_protocolo_w::text <> '') then
			select  substr(coalesce(max(obter_desc_protocolo(cd_protocolo_w)),' '),1,500),
			substr(coalesce(max(obter_desc_protocolo_medic(nr_seq_medicacao_w, cd_protocolo_w)),' '),1,500)
			into STRICT  ds_protocolo_w,
			ds_medic_w
			;
			if (ds_protocolo_w <> ' ') and (ds_medic_w <> ' ') then
				ds_retorno_w := substr((ds_protocolo_w ||' / '||ds_medic_w),1,500);

			elsif (ds_protocolo_w <> ' ') then
				ds_retorno_w := substr((ds_protocolo_w),1,500);
			end if;
		end if;
	end if;
	
	return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
