SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_protocolo_convenio_etapa ( nr_seq_protocolo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_etapa_w		bigint;
ds_etapa_w		varchar(80);
dt_etapa_w		timestamp;
nm_usuario_w		varchar(15);
ds_observacao_w		varchar(255);

C01 CURSOR FOR
	SELECT	a.nr_seq_etapa,
		b.ds_etapa,
		a.dt_etapa,
		coalesce(a.nm_usuario_nrec, a.nm_usuario),
		a.ds_observacao
	from	protocolo_convenio_etapa a,
		fatur_protocolo_etapa b
	where	a.nr_seq_etapa = b.nr_sequencia
	and	a.nr_seq_protocolo = nr_seq_protocolo_p
	order by a.dt_etapa;

/* ie_opcao_p =
C - Seq. Etapa
D - Desc. Etapa
T - Data Etapa
U - Usuário
O - Observação da etapa
*/
BEGIN

if (coalesce(nr_seq_protocolo_p,0) > 0) then

	open C01;
	loop
	fetch C01 into
		nr_seq_etapa_w,
		ds_etapa_w,
		dt_etapa_w,
		nm_usuario_w,
		ds_observacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		nr_seq_etapa_w	:= nr_seq_etapa_w;
		ds_etapa_w	:= ds_etapa_w;
		dt_etapa_w	:= dt_etapa_w;
		nm_usuario_w	:= nm_usuario_w;
		ds_observacao_w	:= ds_observacao_w;
		end;
	end loop;
	close C01;

	if (ie_opcao_p = 'C') then
		ds_retorno_w	:= nr_seq_etapa_w;
	elsif (ie_opcao_p = 'D') then
		ds_retorno_w	:= ds_etapa_w;
	elsif (ie_opcao_p = 'T') then
		ds_retorno_w	:= dt_etapa_w;
	elsif (ie_opcao_p = 'U') then
		ds_retorno_w	:= nm_usuario_w;
	elsif (ie_opcao_p = 'O') then
		ds_retorno_w	:= ds_observacao_w;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
