CREATE OR REPLACE FUNCTION obter_protocolo_descricao (cd_protocolo_p bigint, nr_seq_protocolo_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_opcao_p text, nr_seq_item_prot_p bigint) RETURNS varchar AS $body$
DECLARE


ds_descricao_w	varchar(254)	:= '';


BEGIN

if (cd_protocolo_p IS NOT NULL AND cd_protocolo_p::text <> '') and (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then

	if (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') and (ie_origem_proced_p IS NOT NULL AND ie_origem_proced_p::text <> '') and (ie_opcao_p	= 'P') then

		select	substr(max(a.ds_prescricao),1,254)
		into STRICT	ds_descricao_w
		from	protocolo_medic_proc a
		where	cd_protocolo 	 = cd_protocolo_p
		and	nr_sequencia 	 = nr_seq_protocolo_p
		and	cd_procedimento  = cd_procedimento_p
		and	ie_origem_proced = ie_origem_proced_p;

	elsif (ie_opcao_p = 'HM') then

		select	substr(max(ds_prescricao),1,254)
		into STRICT	ds_descricao_w
		from	prot_solic_bco_sangue
		where	cd_protocolo 	 = cd_protocolo_p
		and		nr_seq_protocolo = nr_seq_protocolo_p
		and		nr_sequencia	= nr_seq_item_prot_p;

	end if;

end if;

return	ds_descricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
