CREATE OR REPLACE FUNCTION obter_protocolo_doc_nf ( nr_seq_nota_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_protocolo_w	bigint;
vl_retorno_w		bigint;


BEGIN

if (nr_seq_nota_p IS NOT NULL AND nr_seq_nota_p::text <> '') then
	select	coalesce(max(b.nr_sequencia), 0)
	into STRICT	nr_seq_protocolo_w
	from	protocolo_doc_item b,
		protocolo_documento a
