CREATE OR REPLACE FUNCTION obter_protocolo_pac_atend (nr_seq_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_protocolo_w	varchar(255);


BEGIN
if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then
	SELECT	MAX(coalesce(SUBSTR(obter_desc_protocolo_medic(a.nr_seq_medicacao, a.cd_protocolo),1,50), ds_protocolo_livre))
	INTO STRICT    ds_protocolo_w
	FROM	paciente_setor a,
		paciente_atendimento b
	WHERE	a.nr_seq_paciente	= b.nr_seq_paciente
	AND 	b.nr_seq_paciente 	= nr_seq_atendimento_p
	AND     coalesce(a.ie_suporte, 'N') 	= 'N';
end if;

return	ds_protocolo_w;

end;
