CREATE OR REPLACE FUNCTION obter_prot_conta_atend ( nr_atendimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(4000)	:= wheb_mensagem_pck.get_texto(309497); -- NÃ£o possui 
nr_protocolo_w	varchar(40);
qt_conta_prot_w	bigint;


BEGIN 
 
select	count(*) 
into STRICT	qt_conta_prot_w 
from	conta_paciente 
where	nr_atendimento	= nr_atendimento_p 
and	(nr_protocolo IS NOT NULL AND nr_protocolo::text <> '');
 
if (qt_conta_prot_w > 0) then 
	ds_Retorno_w	:= wheb_mensagem_pck.get_texto(309498); -- Possui 
end if;
 
return	ds_retorno_w;
 
end;
