CREATE OR REPLACE FUNCTION obter_proxima_dose_medic ( dt_horario_p timestamp, cd_intervalo_p text, ds_horarios_p text, ie_adiciona_dia_p text) RETURNS timestamp AS $body$
DECLARE

ie_lib_auto_w		varchar(1);
ie_dia_semana_w		varchar(1);
ie_segunda_w		intervalo_prescricao.ie_segunda%type;
ie_terca_w			intervalo_prescricao.ie_terca%type;
ie_quarta_w			intervalo_prescricao.ie_quarta%type;
ie_quinta_w			intervalo_prescricao.ie_quinta%type;
ie_sexta_w			intervalo_prescricao.ie_sexta%type;
ie_sabado_w			intervalo_prescricao.ie_sabado%type;
ie_domingo_w		intervalo_prescricao.ie_domingo%type;
ie_operacao_w		intervalo_prescricao.ie_operacao%type;
qt_dia_adic_w		bigint := 0;
qt_operacao_w		bigint;
dt_horario_w		timestamp;
cont_w				smallint := 0;
ds_hor_w			varchar(255);


BEGIN

if (ie_adiciona_dia_p = 'S') then
	dt_horario_w := dt_horario_p + 1;
else
	dt_horario_w := dt_horario_p;
end if;

ds_hor_w := obter_menor_horario(ds_horarios_p);
if	coalesce(ds_hor_w::text, '') = '' then
	ds_hor_w := to_char(dt_horario_p, 'hh24:mi');
end if;

select	max(ie_segunda),
	max(ie_terca),
	max(ie_quarta),
	max(ie_quinta),
	max(ie_sexta),
	max(ie_sabado),
	max(ie_domingo),
	max(ie_operacao)
into STRICT	ie_segunda_w,
	ie_terca_w,
	ie_quarta_w,
	ie_quinta_w,
	ie_sexta_w,
	ie_sabado_w,
	ie_domingo_w,
	ie_operacao_w
from	intervalo_prescricao
where	cd_intervalo	= cd_intervalo_p;

loop
	select	pkg_date_utils.get_WeekDay(dt_horario_w)
	into STRICT	ie_dia_semana_w
	;

	if (ie_dia_semana_w	= 1) and (ie_domingo_w		= 'S') then
		exit;
	elsif (ie_dia_semana_w	= 2) and (ie_segunda_w		= 'S') then
		exit;
	elsif (ie_dia_semana_w	= 3) and (ie_terca_w		= 'S') then
		exit;
	elsif (ie_dia_semana_w	= 4) and (ie_quarta_w		= 'S') then
		exit;
	elsif (ie_dia_semana_w	= 5) and (ie_quinta_w		= 'S') then
		exit;
	elsif (ie_dia_semana_w	= 6) and (ie_sexta_w		= 'S') then
		exit;
	elsif (ie_dia_semana_w	= 7) and (ie_sabado_w		= 'S') then
		exit;
	end if;
	dt_horario_w	:= dt_horario_w + 1;
	cont_w		:= cont_w + 1;
	if (cont_w >= 7) then
		dt_horario_w := dt_horario_p + 1;
		exit;
	end if;
end loop;

return trunc(ESTABLISHMENT_TIMEZONE_UTILS.dateAtTime(dt_horario_w, coalesce(ds_hor_w, '00:00')),'mi');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
