SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_proximo_depois (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registros_w		bigint;
ds_retorno_w		varchar(1) := 'S';
cd_versao_w			varchar(15);
ie_depois_w			varchar(1);
ie_interno_w			varchar(1);
qt_arquivo_depois_w	bigint;
nr_depois_w			bigint;
nr_depois_atual_w	bigint;


BEGIN

select	max(cd_versao), /*Verificar a versão em que está sendo gerado o Depois.sql e se o arquivo é um "depois" ou "interno" */
		max(CASE WHEN position('DEPOIS' in UPPER(ds_titulo))=0 THEN 'N'  ELSE 'S' END ),
		max(somente_numero(ds_titulo)),
		max(CASE WHEN position('INTERNO' in UPPER(ds_titulo))=0 THEN 'N'  ELSE 'S' END )
into STRICT	cd_versao_w,
		ie_depois_w,
		nr_depois_w,
		ie_interno_w
from	versao_alteracao
where	nr_sequencia = nr_sequencia_p;

select	count(*) /*Verificar se já existem outros registros além do atual para a versão em questão*/
into STRICT	qt_registros_w
from	versao_alteracao
where	nr_sequencia <> nr_sequencia_p
and		cd_versao = cd_versao_w;

if (ie_depois_w = 'N' and ie_interno_w = 'N') then
		ds_retorno_w := 'N';
		qt_registros_w := 0;
end if;

if (qt_registros_w > 0) then /* Se houver algum registro de depois, ver a sequência*/
	if (ie_depois_w = 'N') then /* Se não houver registros de depois, ver os "interno" */
		SELECT max(somente_numero(ds_titulo))
		into STRICT	nr_depois_atual_w
		from	versao_alteracao
		where	nr_sequencia <> nr_sequencia_p
		and		cd_versao = cd_versao_w
		and		position('INTERNO' in UPPER(ds_titulo)) <> 0;
	else
		SELECT max(somente_numero(ds_titulo)) /*Senão verificar os depois*/
		into STRICT	nr_depois_atual_w
		from	versao_alteracao
		where	nr_sequencia <> nr_sequencia_p
		and		cd_versao = cd_versao_w
		and		position('DEPOIS' in UPPER(ds_titulo)) <> 0;
	end if;

	if (nr_depois_w <> (nr_depois_atual_w+1)) then
		ds_retorno_w := 'N';
	else
		ds_retorno_w := 'S';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
