CREATE OR REPLACE FUNCTION obter_proximo_dia_nao_util (cd_estabelecimento_p bigint, dt_referencia_p timestamp) RETURNS timestamp AS $body$
DECLARE


dt_referencia_w		timestamp;
dt_retorno_w		timestamp;
ie_feriado_w		smallint;
qt_dia_semana_w		smallint;


BEGIN
dt_referencia_w		:= dt_referencia_p;
dt_retorno_w		:= null;

if (dt_referencia_w IS NOT NULL AND dt_referencia_w::text <> '') then
	while(coalesce(dt_retorno_w::text, '') = '') loop
		qt_dia_semana_w	:= pkg_date_utils.get_WeekDay(dt_referencia_w);

		ie_feriado_w	:= obter_se_feriado(cd_estabelecimento_p, dt_referencia_w);

		if	((ie_feriado_w = 1) or (qt_dia_semana_w in (1,7))) then
			dt_retorno_w	:= dt_referencia_w;
		else
			dt_referencia_w	:= dt_referencia_w + 1;
		end if;
	end loop;
end if;

return dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
