CREATE OR REPLACE FUNCTION obter_proximo_laudo_atx (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE
nr_laudo_w bigint;


BEGIN

select 	coalesce(max(nr_laudo),0) + 1
into STRICT	nr_laudo_w
from 	laudo_paciente
where 	nr_atendimento = nr_atendimento_p;

return	nr_laudo_w;

end;
