CREATE OR REPLACE FUNCTION obter_proximo_setor ( nr_seq_interno_p bigint) RETURNS bigint AS $body$
DECLARE


cd_setor_atendimento_w	integer;
cd_proximo_setor_w	integer;
nr_atendimento_w		bigint;
nr_seq_interno_w		bigint;


BEGIN

/* Obter o setor do parâmetro*/

select	max(cd_setor_atendimento),
	max(nr_atendimento)
into STRICT	cd_setor_atendimento_w,
	nr_atendimento_w
from	atend_paciente_unidade
where	nr_seq_interno = nr_seq_interno_p;

/* Obter a próxima movimentação do pac no atendimento apos o atual */

select	coalesce(min(nr_seq_interno),0)
into STRICT	nr_seq_interno_w
from	atend_paciente_unidade
where	nr_atendimento	= nr_atendimento_w
and	nr_seq_interno	> nr_seq_interno_p;

if (nr_seq_interno_w > 0) then
	select	coalesce(a.cd_setor_atendimento,cd_setor_atendimento_w)
	into STRICT	cd_proximo_setor_w
	from	atend_paciente_unidade a
	where	nr_atendimento	= nr_atendimento_w
	and	nr_seq_interno	= nr_seq_interno_w;
else
	cd_proximo_setor_w	:= 0;
end if;

return cd_proximo_setor_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_proximo_setor ( nr_seq_interno_p bigint) FROM PUBLIC;

