SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_proximo_status_painel ( nr_seq_agenda_p bigint, nr_cirurgia_p bigint) RETURNS bigint AS $body$
DECLARE



dt_chegada_w			timestamp;
dt_chegada_fim_w		timestamp;
dt_sala_recuperacao_w		timestamp;
dt_alta_cc_w			timestamp;
dt_saida_paciente_cc_w		timestamp;
dt_analise_w			timestamp;
dt_chamada_pre_inducao_w	timestamp;
dt_paciente_aguardo_w		timestamp;
dt_paciente_termino_w		timestamp;
ie_status_agenda_w		varchar(15);
ie_evento_painel_ww		varchar(15);
ie_evento_painel_w		varchar(15);
nr_seq_cor_w			bigint;

c01 CURSOR FOR
	SELECT	ie_evento_painel
	from	evento_cirurgia_paciente a,
		evento_cirurgia b
	where	a.nr_seq_evento = b.nr_sequencia
	and	a.nr_cirurgia 	= nr_cirurgia_p
	and	coalesce(a.ie_situacao,'A') = 'A'
	and	coalesce(b.ie_situacao,'A') = 'A'
	and	(ie_evento_painel IS NOT NULL AND ie_evento_painel::text <> '')
	order by nr_seq_execucao;


BEGIN

if (coalesce(nr_cirurgia_p,0) > 0) then
	SELECT	max(dt_chegada),
		max(dt_chegada_fim),
		max(dt_sala_recuperacao),
		max(dt_alta_cc),
		max(dt_saida_paciente_cc),
		max(dt_analise),
		max(dt_chamada_pre_inducao),
		max(dt_paciente_aguardo),
		max(dt_paciente_termino),
		max(ie_status_agenda)
	INTO STRICT	dt_chegada_w,
		dt_chegada_fim_w,
		dt_sala_recuperacao_w,
		dt_alta_cc_w,
		dt_saida_paciente_cc_w,
		dt_analise_w,
		dt_chamada_pre_inducao_w,
		dt_paciente_aguardo_w,
		dt_paciente_termino_w,
		ie_status_agenda_w
	FROM	agenda_paciente
	WHERE	nr_sequencia	=	nr_seq_agenda_p;

	open C01;
	loop
	fetch C01 into
		ie_evento_painel_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ie_evento_painel_ww := ie_evento_painel_w;
		end;
	end loop;
	close C01;


	if (ie_evento_painel_ww = 'DC') and (dt_chegada_w IS NOT NULL AND dt_chegada_w::text <> '') then
		nr_seq_cor_w	:= 615;
	--elsif	(ie_evento_painel_ww = 'E') and (ie_status_agenda_w = 'E') then
--		nr_seq_cor_w	:= 615;
	elsif (ie_evento_painel_ww = 'DCF') and (dt_chegada_fim_w IS NOT NULL AND dt_chegada_fim_w::text <> '') then
		nr_seq_cor_w	:= 617;
	elsif (ie_evento_painel_ww = 'IN') and (dt_analise_w IS NOT NULL AND dt_analise_w::text <> '') then
		nr_seq_cor_w	:= 1047;
	elsif (ie_evento_painel_ww = 'DPA') and (dt_paciente_aguardo_w IS NOT NULL AND dt_paciente_aguardo_w::text <> '') then
		nr_seq_cor_w	:= 1912;
	elsif (ie_evento_painel_ww = 'DAC') and (dt_alta_cc_w IS NOT NULL AND dt_alta_cc_w::text <> '') then
		nr_seq_cor_w	:= 856;
	elsif (ie_evento_painel_ww = 'DCP') and (dt_chamada_pre_inducao_w IS NOT NULL AND dt_chamada_pre_inducao_w::text <> '') then
		nr_seq_cor_w	:= 1156;
	elsif (ie_evento_painel_ww = 'DSR') and (dt_sala_recuperacao_w IS NOT NULL AND dt_sala_recuperacao_w::text <> '') then
		nr_seq_cor_w	:= 855;
	elsif (ie_evento_painel_ww = 'DS')  and (dt_saida_paciente_cc_w IS NOT NULL AND dt_saida_paciente_cc_w::text <> '') then
		nr_seq_cor_w	:= 1439;
	end if;
end if;

return nr_seq_cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
