SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_proximo_valor_retorno ( nr_seq_retorno_p bigint, nr_interno_conta_p bigint, cd_autorizacao_p text, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
	0 = Valor pago do proximo retorno
	1 = Data do proximo retorno*/
nr_seq_retorno_w	bigint;
vl_pago_w		double precision := 0;
nr_seq_item_w		bigint;
dt_retorno_w		timestamp;
ds_retorno_w		varchar(100);


BEGIN

select	min(nr_seq_retorno)
into STRICT	nr_seq_retorno_w
from	convenio_retorno_item
where	nr_seq_retorno 		> nr_seq_retorno_p
and	nr_interno_conta 	= nr_interno_conta_p
and	coalesce(vl_pago, 0)		> 0
and	cd_autorizacao 		= cd_autorizacao_p;

if (nr_seq_retorno_w > 0)	then

	select 	max(nr_sequencia)
	into STRICT	nr_seq_item_w
	from	convenio_retorno_item
	where	nr_seq_retorno		= nr_seq_retorno_w
	and	nr_interno_conta	= nr_interno_conta_p
	and	cd_autorizacao		= cd_autorizacao_p;

	select	coalesce(a.vl_pago,0),
		b.dt_retorno
	into STRICT	vl_pago_w,
		dt_retorno_w
	from	convenio_retorno_item a,
		convenio_retorno b
	where	a.nr_seq_retorno	= b.nr_sequencia
	and	a.nr_sequencia		= nr_seq_item_w;
end if;

if (ie_opcao_p = 0)	then
	ds_retorno_w 	:= vl_pago_w;

elsif (ie_opcao_p = 1)	then
	ds_retorno_w	:= to_char(dt_retorno_w, 'dd/mm/yyyy');
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
