CREATE OR REPLACE FUNCTION obter_prox_data_ger_build ( qt_dia_semana_obter_p bigint, qt_dia_atual_p bigint, dt_base_p timestamp) RETURNS timestamp AS $body$
DECLARE


dt_prox_w	timestamp;


BEGIN

if (qt_dia_semana_obter_p >= qt_dia_atual_p) then
	begin

	select	dt_base_p + (qt_dia_semana_obter_p - qt_dia_atual_p)
	into STRICT	dt_prox_w
	;


	end;

else
	begin

	select	dt_base_p + (7 + qt_dia_semana_obter_p - qt_dia_atual_p)
	into STRICT	dt_prox_w
	;

	end;
end if;

return	dt_prox_w;

end;
$body$
