SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_prox_data_programador (nm_usuario_prev_p text, qt_minutos_diario_p bigint, nr_seq_gerencia_p bigint) RETURNS timestamp AS $body$
DECLARE

 
dt_vago_w		timestamp;
dt_consulta_w	timestamp;
qt_atividades_w		bigint;
qt_min_previsto_w	bigint;
ie_dia_util_w		varchar(1);


BEGIN 
 
dt_consulta_w	:= trunc(clock_timestamp());
 
while(coalesce(dt_vago_w::text, '') = '') loop 
	select	max(a.qt_min_previsto) 
	into STRICT	qt_min_previsto_w 
	from	usuario b, 
	    man_previsao_dia a 
	where  a.nm_usuario_prev = b.nm_usuario 
	and   b.ie_situacao <> 'I' 
	and		a.nm_usuario_prev = nm_usuario_prev_p 
	and   trunc(a.dt_dia_previsao) = dt_consulta_w 
	and exists (	SELECT	1 
				from 	gerencia_wheb_grupo x 
	      where 	x.nr_sequencia = a.nr_seq_grupo_gerencia 
	      and  	x.nr_seq_gerencia = nr_seq_gerencia_p);
				 
	select	count(*) 
	into STRICT	qt_atividades_w 
	from	man_os_prevista_v a 
	where	a.nm_usuario_prev = nm_usuario_prev_p 
	and		a.dt_prevista between trunc(to_date(dt_consulta_w),'dd') and fim_dia(to_date(dt_consulta_w)) 
	and (coalesce(a.ie_situacao_os,'0') <> '3' 
			or exists (	SELECT	1 
						from	man_ordem_serv_ativ x 
						where	x.nr_seq_ordem_serv 	= a.nr_sequencia 
						and		x.nm_usuario_exec	= a.nm_usuario_prev 
						and		trunc(x.dt_atividade,'dd') = trunc(a.dt_prevista,'dd')));
 
	if (qt_atividades_w < 2) or (qt_min_previsto_w < qt_minutos_diario_p) then 
		dt_vago_w := dt_consulta_w;
	else 
		dt_consulta_w	:= trunc(dt_consulta_w) + 1;
		 
		ie_dia_util_w	:= Obter_Se_Dia_Util(dt_consulta_w,1);
		while(ie_dia_util_w = 'N') loop 
			dt_consulta_w	:= trunc(dt_consulta_w) + 1;
			ie_dia_util_w	:= Obter_Se_Dia_Util(dt_consulta_w,1);
		end loop;
	end if;
	 
end loop;
 
return	dt_vago_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
