CREATE OR REPLACE FUNCTION obter_prox_data_solic (ie_forma_solic_p text, dt_referencia_p timestamp, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


dt_base_w		timestamp;
vl_mes_w		smallint;
nr_mes_w		smallint;
