nr_mes_ref_w	smallint;


BEGIN


nr_dia_ref_w	:= (pkg_date_utils.extract_field('DAY', dt_referencia_p, 0))::numeric;
nr_mes_ref_w	:= (pkg_date_utils.extract_field('MONTH', dt_referencia_p, 0))::numeric;

if (ie_forma_solic_p IS NOT NULL AND ie_forma_solic_p::text <> '') and (nr_dia_ref_w IS NOT NULL AND nr_dia_ref_w::text <> '') and (nr_mes_ref_w IS NOT NULL AND nr_mes_ref_w::text <> '') then

	vl_mes_w	:= nr_mes_ref_w;

	if (ie_forma_solic_p = 'M') then

		nr_mes_w	:= 1;
		vl_mes_w	:= (pkg_date_utils.extract_field('MONTH', PKG_DATE_UTILS.ADD_MONTH(dt_referencia_p,0, 0), 0))::numeric;

	elsif (ie_forma_solic_p = 'T') then

		nr_mes_w	:= 3;

	elsif (ie_forma_solic_p = 'S') then

		nr_mes_w 	:= 6;

	elsif (ie_forma_solic_p = '2A') then

		nr_mes_w 	:= 24;

	elsif (ie_forma_solic_p = '4A') then

		nr_mes_w 	:= 48;

	else
		nr_mes_w 	:= 12;

	end if;

	dt_param_w	:= dt_referencia_p;
        dt_param_w 	:= PKG_DATE_UTILS.ADD_MONTH(dt_param_w,nr_mes_w, 0);

	dt_base_w	:= dt_param_w;

end if;

return dt_base_w;

end;
