CREATE OR REPLACE FUNCTION obter_prox_hora_cheia ( dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


hr_referencia_w		varchar(10);
ds_retorno_w		varchar(10);


BEGIN

hr_referencia_w := substr(to_char(dt_referencia_p,'dd/mm/yyyy hh24:mi:ss'),15,2);

if (hr_referencia_w = '00') then
	ds_retorno_w := substr(to_char(dt_referencia_p,'dd/mm/yyyy hh24:mi:ss'),12,5);
elsif (hr_referencia_w <> '00') then
	ds_retorno_w := substr(to_char(trunc(dt_referencia_p + 1/24,'hh24'),'dd/mm/yyyy hh24:mi:ss'),12,5);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
