CREATE OR REPLACE FUNCTION obter_prox_hr_gerar_mapa () RETURNS timestamp AS $body$
DECLARE


dt_retorno_w	timestamp;


BEGIN

select	min(to_date(to_char(clock_timestamp(),'dd/mm/yyyy')||ds_horario_auto,'dd/mm/yyyy hh24:mi:ss'))
into STRICT	dt_retorno_w
from	horario_refeicao_dieta
where	(ds_horario_auto IS NOT NULL AND ds_horario_auto::text <> '')
and	to_date(to_char(clock_timestamp(),'dd/mm/yyyy')||ds_horario_auto,'dd/mm/yyyy hh24:mi:ss') > clock_timestamp();

if (coalesce(dt_retorno_w::text, '') = '') then
	select	min(to_date(to_char(clock_timestamp() + interval '1 days','dd/mm/yyyy')||ds_horario_auto,'dd/mm/yyyy hh24:mi:ss'))
	into STRICT	dt_retorno_w
	from	horario_refeicao_dieta
	where	(ds_horario_auto IS NOT NULL AND ds_horario_auto::text <> '');
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
